import React,{useState} from 'react'
import { AiOutlineClose, AiOutlineMenu } from 'react-icons/ai';
import { FaShoppingBasket } from "react-icons/fa";


const Navbar = () => {

  const [nav, setNav] = useState(false);

  const handleNav = () => {
    setNav(!nav);
  };

  return (
    <div className='flex justify-between items-center  max-w-[1240px] p-8 px-4 background-color: transparent'>
      <h1 className='text-xl ml-6 font-bold '>Shopping</h1>
        <ul className='hidden md:flex '>
            <li className='ml-8 '>Products</li>
            <li className='ml-8'>Offers</li>
            <li className='ml-8'>Contact</li>
            <FaShoppingBasket size={30} className="ml-8" />
                       
        </ul>
        

        <div onClick={handleNav} className='block md:hidden'>
          {nav ? <AiOutlineClose size={20}/> : <AiOutlineMenu size={20} />}
      </div>
      <ul className={nav ? 'fixed left-0 top-0 w-[60%] bg-black text-white h-50% border-r border-r-gray-900  ease-in-out duration-500' : 'ease-in-out duration-500 fixed left-[-100%]'}>
        <li className='p-4'>Products</li>
        <li className='p-4'>Offers</li>       
        <li className='p-4'>Contact</li>
        <FaShoppingBasket size={30} className="p-8" />     
      </ul>

    </div>
  )
}

export default Navbar
