import React from 'react'
import Navbar from '../components/Navbar'
import Footer from '../components/Footer'
import { useDispatch,useSelector } from 'react-redux';


const Cart = () => {
  const products = useSelector((state) => state.cartReducer.products)
  const total = useSelector((state) => state.cartReducer.total);
  return (
    <div>
      <Navbar />
      <div className='flex w-full bg-orange-500 h-screen'>
        <div className='m-auto  w-3/4 h-3/4 bg-slate-100'>
          <div className='flex'>
            {products.length  ? (products.map(item => {
              return(
                <div className='border flex flex-row   justify-between '>
                  <div>
                    <h1>{item.title}</h1>
                    <img src={item.image} className=' w-20 h-20  ' />                  
                  </div>
                  <div>
                  <h1>Quantity: {item.quantity}</h1>
                  </div>
                  <div className='grid grid-cols-2'>
                  <h1>{item.subTotal}$</h1>
                  <button className='w-8 h-8 ml-2 rounded-md  bg-[#0be881] text-white font-bold text-2xl'>X</button>
                  </div>
                  

                </div>
              )
            })) 
            
            : (<div> No Products </div>)}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  )
}

export default Cart
